import React from 'react'
import styled from 'styled-components'
import Navbar from './Navbar';
import Card from './Card';
function Home({basket,setBasket}) {
  
  
  
  return (
   <Container>
  <Navbar basket={basket}/>
  <Banner>
  <img src="./banner.jpg" alt="" />
  <img src="./mobile_banner.jpg" alt="" />
  </Banner>
  <Main>
<Card basket={basket} setBasket={setBasket} image={"./yashpal.jpg"} price={2500} rating={3} title={"Echo Dot"} />
<Card basket={basket} setBasket={setBasket} image={"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1563378212-amazon-echo-dot-3-1563376152.jpg?crop=1xw:1xh;center,top&resize=768:*"} price={2500} rating={3} title={"Echo Dot"}/>
<Card basket={basket} setBasket={setBasket} image={"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1563378212-amazon-echo-dot-3-1563376152.jpg?crop=1xw:1xh;center,top&resize=768:*"} price={2500} rating={3} title={"Echo Dot"}/>
<Card basket={basket} setBasket={setBasket} image={"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1563378212-amazon-echo-dot-3-1563376152.jpg?crop=1xw:1xh;center,top&resize=768:*"} price={2500} rating={3} title={"Echo Dot"}/>

  </Main>
   </Container>
  )
}

const Container = styled.div`
width: 100%;
background-color: rgb(234,237,237);
`;

const Banner = styled.div`
  width: 100%;
  img {
    width: 100%;
    -webkit-mask-image: linear-gradient(
      to bottom,
      rgba(0, 0, 0, 2),
      rgba(0, 0, 0, 0.95),
      rgba(0, 0, 0, 0.85),
      rgba(0, 0, 0, 0.75),
      rgba(0, 0, 0, 0.55),
      rgba(0, 0, 0, 0)
    );
    &:nth-child(2) {
      display: none;
    }
    @media only screen and (max-width: 767px) {
      &:nth-child(1) {
        display: none;
      }
      &:nth-child(2) {
        display: block;
        -webkit-mask-image: none;
      }
    }
  }
`;

const Main = styled.div`
display: grid;
justify-content: center;
place-items: center;
width: 100%;

grid-auto-rows: 420px;
  grid-template-columns: repeat(4, 280px);
  grid-gap: 20px; 

${'' /* Mobile  */}
@media only screen and (max-width:767px){
  grid-template-columns: repeat(2,50%);
  grid-gap:0;
}

@media only screen and (min-width:767px) and (max-width: 1200px){
  grid-template-columns: repeat(3,30%);
}

@media only screen and (min-width:767px){
  margin-top: -130px;
padding: 10px 0px;
}

`;

export default Home
