import React from 'react'
import styled from 'styled-components'
import Rating from '@mui/material/Rating';

function Card({basket,setBasket,image,title,price,rating}){
    return(
        <Container>
        <Image>
    <img src='https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1563378212-amazon-echo-dot-3-1563376152.jpg?crop=1xw:1xh;center,top&resize=768:*' alt=''/>
        </Image>
        <Description>
            <h5>Echo Dot</h5>
<Rating name="half-rating-read" defaultValue={4.5} precision={0.5} readOnly />
        <p>$2300</p>
        <button>Add To Cart</button>
        </Description>
        </Container>
    )
}

const Container= styled.div`
width: 100%;
height: 100%;
display:flex;
flex-direction:column;
background-color: #fff;
z-index:10;


`;

const Image=styled.div`
width: 100%;
display:flex;
flex-direction: column;
justify-content: center;
align-items: center;
margin-top: 20px;
flex: 0.3;
img{
    width:180px;
    height:200px;
}
`;
const Description= styled.div`
width: 90%;
margin: auto;
display:flex;
flex-direction: column;
justify-content: space-evenly;
flex:0.7;

h5{
    font-size: 16px;
    font-weight: 600;
}

p{
  font-weight: 600;  
}

button{
    width: 100%;
    height: 33px;
    background-color: #fa8900;
    border:none;
    border-radius: 10px;
    cursor:pointer;
}
`;

export default Card
