import React from "react";
import styled from "styled-components";

function SignUp(){
    return (
        <Container>
        Welcome to Sign up page
        </Container>
    )
}

const Container =styled.div`
width: 40%;
min-width: 450px;
height: fit-content;
padding: 15px;
margin: auto;
display: flex;
flex-direction: column;
align-items: center;

`;

const Logo=styled.div`
width:120px;

img {
width:100%;
}
`;

const FormContainer =styled.form`
border: 1px solid lightgrey;
width: 55%;
height: 400px;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
padding: 15px;

h3{
    font-size: 28px;
    font-weight: 400;
    line-height: 33px;
    align-self: flex-start;
    margin-bottom: 10px;
}

`;

const InputContainer= styled.div`
width:100%;
padding:10px;

p {
    font-size:14px;
    font-weight:600;
}

input {
    width: 95%;
    height: 33px;
    padding-left: 5px;
    border-radius: 5px;
    border: 1px solid lightgray;
    margin-top:5%;

&:hover {
    border: 1px solid orange;

}
}
`;

export default SignUp
